import React, { useEffect, useState } from 'react';
import {
    Card,
    Button,
    CardTitle,
    CardText,
    Row,
    Col,
    Container,
    ButtonDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
} from 'reactstrap';
import { connect } from 'react-redux';
import { loadItemsData, loadCategoriesData, filterItems } from '../../actions/items';
import { Item, Category } from '../../reducers/types';
import { addItemToCart } from '../../actions/cart';

function Home(props: any): JSX.Element {
    useEffect(() => {
        props.loadItemsData();
        props.loadCategoriesData();
    }, []);

    const [dropdownOpen, setOpen] = useState(false);

    const toggle = (): void => setOpen(!dropdownOpen);

    let filteredItems = [];
    const frontendFilter = (id: number): void => {
        filteredItems = props.items.filter((item: Item) => item.categories.include(id));
    };
    const items = props.items.map((item: Item) => (
        <Col key={item.name} sm="4" style={{ padding: '10px' }}>
            <Card body>
                <CardTitle>
                    <h4>{item.name}</h4>
                </CardTitle>
                <CardText>{item.descr}</CardText>
                <Button onClick={(): void => props.addItemToCart(item)}>ADD TO CART</Button>
            </Card>
        </Col>
    ));

    const filterItems = props.categories.map((category: Category) => (
        <DropdownItem key={category.id} onClick={(): void => props.filterItems(category.id)}>
            {category.title}
        </DropdownItem>
    ));

    return (
        <Container>
            <Row>
                <ButtonDropdown isOpen={dropdownOpen} toggle={toggle}>
                    <DropdownToggle caret>Filter by</DropdownToggle>
                    <DropdownMenu>{filterItems}</DropdownMenu>
                </ButtonDropdown>
            </Row>
            <Row>{items}</Row>
        </Container>
    );
}

const mapStateToProps = (state: any) => {
    console.log(state);
    return {
        items: state.items.items,
        categories: state.items.categories,
    };
};

export default connect(mapStateToProps, { loadItemsData, addItemToCart, loadCategoriesData, filterItems })(Home);
