export const LOAD_ITEMS = 'LOAD_ITEMS';
export const LOAD_CATEGORIES = 'LOAD_CATEGORIES';
export const LOAD_ITEM_TO_CART = 'LOAD_ITEM_TO_CART';

export interface Item {
    id: number;
    name: string;
    descr: string;
    cost: number;
    categories: number[];
}

export interface Category {
    id: number;
    title: string;
}

export interface ItemsState {
    items: Item[];
    categories: Category[];
}

interface LoadItems {
    type: typeof LOAD_ITEMS;
    payload: Item[];
}

interface LoadCategories {
    type: typeof LOAD_CATEGORIES;
    payload: Category[];
}

export interface CartItem {
    id: number;
    name: string;
    descr: string;
    cost: number;
    categories: number[];
}

export type ItemsActionTypes = LoadItems | LoadCategories;

/**
 * Cart
 */

export interface LoadItemToCart {
    type: typeof LOAD_ITEM_TO_CART;
    payload: Item;
}
