import React from 'react';
import Link from 'next/link';
import { connect } from 'react-redux';

const Header = (props: any): JSX.Element => (
    <header
        style={{
            background: `hsl(208, 94%, 68%)`,
            marginBottom: `1.45rem`,
        }}
    >
        <div
            style={{
                margin: `0 auto`,
                maxWidth: 960,
                padding: `1.45rem 1.0875rem`,
            }}
        >
            <Link href="/">
                <h1 style={{ margin: 0, color: `white`, display: `inline-block`, cursor: 'pointer' }}>Test Store </h1>
            </Link>

            <Link href="/cart">
                <h2 style={{ margin: 0, color: `white`, display: `inline-block`, float: `right`, cursor: 'pointer' }}>
                    Cart {props.items.length}
                </h2>
            </Link>
        </div>
    </header>
);

const mapStateToProps = (state: any) => {
    return {
        items: state.cart.items,
    };
};

export default connect(mapStateToProps, null)(Header);
