import React, { useEffect } from 'react';
import { Card, Button, CardTitle, CardText, Row, Col, Container, Table } from 'reactstrap';
import { connect } from 'react-redux';
import { loadItemsData } from '../../actions/items';
import { Item } from '../../reducers/types';

function Cart(props: any): JSX.Element {
    useEffect(() => {
        props.loadItemsData();
    }, []);

    console.log(props);

    const cartItems = props.items.map((item: Item) => (
        <tr key={item.name}>
            <th scope="row">1</th>
            <td>{item.id}</td>
            <td>{item.name}</td>
            <td>@mdo</td>
        </tr>
    ));

    return (
        <Container>
            <Row>
                <Table>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Username</th>
                        </tr>
                    </thead>
                    <tbody>{cartItems}</tbody>
                </Table>
            </Row>
        </Container>
    );
}

const mapStateToProps = (state: any) => {
    console.log(state);
    return {
        items: state.cart.items,
    };
};

export default connect(mapStateToProps, { loadItemsData })(Cart);
