import React, { useEffect } from 'react';
import { Card, Button, CardTitle, CardText, Row, Col, Container } from 'reactstrap';
import { connect } from 'react-redux';
import { loadItemsData } from '../../actions/items';
import { Item } from '../../reducers/types';
import { addItemToCart } from '../../actions/cart';

const addToCart = (item: Item) => {
    addToCart(item);
};

function Home(props: any): JSX.Element {
    useEffect(() => {
        props.loadItemsData();
    }, []);

    console.log(props);
    const items = props.items.map((item: Item) => (
        <Col key={item.name} sm="4" style={{ padding: '10px' }}>
            <Card body>
                <CardTitle>
                    <h4>{item.name}</h4>
                </CardTitle>
                <CardText>{item.descr}</CardText>
                <Button onClick={() => addToCart(item)}>ADD TO CART</Button>
            </Card>
        </Col>
    ));

    return (
        <Container>
            <Row>{items}</Row>
        </Container>
    );
}
import { format } from 'path';

const mapStateToProps = (state: any) => {
    return {
        items: state.items.items,
    };
};

export default connect(mapStateToProps, { loadItemsData })(Home);
