import { ADD_ITEM_TO_CART, Item } from '../reducers/types';

export function addItemToCart(item: Item) {
    return (dispatch: any) => {
        console.log(item);
        dispatch({ type: ADD_ITEM_TO_CART, payload: item });
    };
}
