import React, { useEffect } from 'react';
import { Card, Button, CardTitle, CardText, Row, CardBody, Container, CardSubtitle } from 'reactstrap';
import { connect } from 'react-redux';
import { useRouter } from 'next/router';
import { deleteItemFromCart } from '../../actions/cart';
import { loadItemsData } from '../../actions/items';
import { Item as ItemType } from '../../reducers/types';

function Item(props: any): JSX.Element {
    useEffect(() => {
        props.loadItemsData();
    }, []);
    const router = useRouter();

    const { id } = router.query;
    const item: ItemType = props.items.filter((item: ItemType) => item.id === parseInt(id[0]))[0] || [];
    return (
        <Container>
            <Row>
                <div>
                    <Card>
                        <CardBody>
                            <CardTitle>{item.name}</CardTitle>
                            <CardSubtitle>{item.descr}</CardSubtitle>
                            <CardText>{item.cost}</CardText>
                            <Button>Add to cart</Button>
                        </CardBody>
                    </Card>
                </div>
            </Row>
        </Container>
    );
}

const mapStateToProps = (state: any) => {
    console.log(777, state);
    return {
        items: state.items.items,
    };
};

export default connect(mapStateToProps, { loadItemsData })(Item);
